--
-- Name: replica_orden(text, text); Type: FUNCTION; Schema: public; Owner: ihsa
--

CREATE FUNCTION public.replica_orden(original text, nueva text) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
	orden_original RECORD;
	orden_nueva RECORD;
	
	auto_orig RECORD;
	autoriza_nueva_id INTEGER;
BEGIN
	SELECT * FROM orden WHERE consecutivo = original INTO orden_original;
	SELECT * FROM autorizaciones_orden WHERE orden = orden_original.id INTO auto_orig;
	
	SELECT * FROM orden WHERE consecutivo = nueva INTO orden_nueva;
	SELECT id FROM autorizaciones_orden WHERE orden = orden_nueva.id INTO autoriza_nueva_id;
	
	RAISE NOTICE 'Orden original % a orden %', orden_original.consecutivo, orden_nueva.consecutivo;
	RAISE NOTICE 'Autorizaciones originales : %', auto_orig;
	RAISE NOTICE 'Nueva autorizacion : %', autoriza_nueva_id;
	
	UPDATE autorizaciones_orden SET
		estatus = 150
		, autoriza_gerencia = auto_orig.autoriza_gerencia
		, fecha_autorizo_gerencia = auto_orig.fecha_autorizo_gerencia
		, hora_autorizo_gerencia = auto_orig.hora_autorizo_gerencia
		, autorizacion_gerencia_auto = auto_orig.autorizacion_gerencia_auto
		, autoriza_compras = auto_orig.autoriza_compras
		, fecha_autorizo_compras = auto_orig.fecha_autorizo_compras
		, hora_autorizo_compras = auto_orig.hora_autorizo_compras
		, autorizacion_compras_auto = auto_orig.autorizacion_compras_auto
		, autoriza_finanzas = auto_orig.autoriza_finanzas
		, fecha_autorizo_finanzas = auto_orig.fecha_autorizo_finanzas
		, hora_autorizo_finanzas = auto_orig.hora_autorizo_finanzas
		, autorizacion_finanzas_auto = auto_orig.autorizacion_finanzas_auto
		, autoriza_mpg = auto_orig.autoriza_mpg
		, fecha_autorizo_mpg = auto_orig.fecha_autorizo_mpg
		, hora_autorizo_mpg = auto_orig.hora_autorizo_mpg
		, autorizacion_mpg_auto = auto_orig.autorizacion_mpg_auto
		, autoriza_ihsa = auto_orig.autoriza_ihsa
		, fecha_autorizo_ihsa = auto_orig.fecha_autorizo_ihsa
		, hora_autorizo_ihsa = auto_orig.hora_autorizo_ihsa
		, autorizacion_ihsa_auto  = auto_orig.autorizacion_ihsa_auto
		, solicito = auto_orig.solicito
		, fecha_solicito = auto_orig.fecha_solicito
		, hora_solicito = auto_orig.hora_solicito
		, autoriza_licitacion = auto_orig.autoriza_licitacion
		, fecha_autorizo_licitacion = auto_orig.fecha_autorizo_licitacion
		, hora_autorizo_licitacion = auto_orig.hora_autorizo_licitacion
		, autorizacion_licitacion_auto = auto_orig.autorizacion_licitacion_auto
		, fecha_envio_proveedor = auto_orig.fecha_envio_proveedor
		, hora_envio_proveedor = auto_orig.hora_envio_proveedor
	WHERE id = autoriza_nueva_id;
	
END;
$$;


