--
-- Name: carta_intencion_pendientes_vw; Type: VIEW; Schema: public; Owner: ihsa
--

CREATE VIEW public.carta_intencion_pendientes_vw AS
 SELECT o.consecutivo,
    ( SELECT lower(COALESCE(array_to_string(array_agg(DISTINCT cp.correo), ', '::text), ''::text)) AS lower
           FROM (public.contactos_orden co
             JOIN public.contacto_proveedor cp ON (((cp.id = co.contacto_proveedor) AND (cp.eliminado = false) AND (cp.notifica = true))))
          WHERE ((co.orden = o.id) AND (co.eliminado = false))) AS contactos,
    lower((u.email)::text) AS analista,
    upper((p.nombre)::text) AS proveedor,
    upper((o.referencia)::text) AS referencia
   FROM (((public.autorizaciones_orden a
     JOIN public.orden o ON ((o.id = a.orden)))
     JOIN public.usuario u ON (((u.id)::text = (o.analista)::text)))
     JOIN public.proveedor p ON ((p.id = o.proveedor)))
  WHERE ((a.eliminado = false) AND (a.estatus = 145) AND ((CURRENT_DATE - COALESCE(a.fecha_autorizo_compras, CURRENT_DATE)) > 3));


