-- Name: destinatarios_correo_vw; Type: VIEW; Schema: public; Owner: ihsa
--

CREATE VIEW public.destinatarios_correo_vw AS
 SELECT lc.id,
    lc.compania,
    lc.ap_campo,
    lc.nombre,
    lc.eliminado,
    COALESCE(u.nombre, u2.nombre) AS usuario,
    COALESCE(u.email, u2.email) AS email
   FROM (((((public.lista_correo lc
     JOIN public.destinatario d ON (((d.lista_correo = lc.id) AND (d.eliminado = false))))
     LEFT JOIN public.usuario u ON ((((d.usuario)::text = (u.id)::text) AND (u.eliminado = false))))
     LEFT JOIN public.si_rol r ON (((d.si_rol = r.id) AND (r.eliminado = false))))
     LEFT JOIN public.si_usuario_rol ur ON (((r.id = ur.si_rol) AND (ur.ap_campo = lc.ap_campo) AND (ur.eliminado = false))))
     LEFT JOIN public.usuario u2 ON ((((ur.usuario)::text = (u2.id)::text) AND (u2.eliminado = false))));


