-- Name: evaluaciones_convenio_vw; Type: VIEW; Schema: public; Owner: ihsa
--

CREATE VIEW public.evaluaciones_convenio_vw AS
 SELECT ce.id,
    ce.cv_evaluacion_template,
    ce.gerencia,
    ce.convenio,
    ce.responsable,
    u.nombre,
    u.email,
    g.nombre AS gnombre,
    p.nombre AS pnombre,
    c.codigo,
    c.ap_campo,
    c.proveedor
   FROM ((((public.cv_convenio_evaluacion ce
     JOIN public.usuario u ON (((u.id)::text = (ce.responsable)::text)))
     JOIN public.convenio c ON ((c.id = ce.convenio)))
     JOIN public.proveedor p ON ((p.id = c.proveedor)))
     JOIN public.gerencia g ON ((g.id = ce.gerencia)))
  WHERE ((ce.eliminado = false) AND (c.estatus = 310))
  GROUP BY ce.id, ce.cv_evaluacion_template, ce.gerencia, ce.convenio, ce.responsable, u.nombre, u.email, g.nombre, p.nombre, c.codigo, c.ap_campo, c.proveedor;


