--
-- Name: facturas_sin_procesar_vw; Type: VIEW; Schema: public; Owner: ihsa
--

CREATE VIEW public.facturas_sin_procesar_vw AS
 SELECT p.rfc,
    p.nombre,
    a.folio,
    a.fecha_genero,
    a.monto,
    ( SELECT COALESCE(array_to_string(array_agg(DISTINCT btrim((cp.correo)::text)), ', '::text), ''::text) AS "coalesce"
           FROM public.contacto_proveedor cp
          WHERE ((cp.eliminado = false) AND (cp.proveedor = p.id))) AS "coalesce"
   FROM ((public.si_factura a
     JOIN public.oc_factura_status fe ON (((fe.si_factura = a.id) AND (fe.eliminado = false) AND (fe.actual = true) AND (fe.estatus = 710))))
     JOIN public.proveedor p ON ((p.id = a.proveedor)))
  WHERE (a.eliminado = false);


