--
-- Name: historial_facturas_vw; Type: VIEW; Schema: public; Owner: ihsa
--

CREATE VIEW public.historial_facturas_vw AS
 SELECT o.id AS id_relacion,
    fac.concepto,
    fac.folio,
    COALESCE(fac.monto, (0)::double precision) AS monto,
    fac.observacion,
    p.id AS id_proveedor,
    p.nombre AS proveedor,
    m.id AS id_moneda,
    m.nombre AS moneda,
    fac.fecha_emision,
    COALESCE(fac.si_adjunto, 0) AS id_adjunto,
    adj.uuid AS uu_id,
    adj.url AS url_archivo,
    fac.id,
    COALESCE(fac.cantidad, 0) AS cantidad,
    adj.nombre AS adjunto,
    adj.fecha_genero,
    fe.id AS id_status_factura,
    fe.estatus AS id_status,
    e.nombre AS status,
    o.consecutivo AS codigo_orden,
    o.navcode AS pedido_nav,
    com.rfc AS rfc_compania,
    com.nombre AS compania,
    fac.revisada AS leida,
    cam.nombre AS campo_orden,
    fac.metodo_pago,
    fac.forma_pago,
    uso.codigo AS codigo_uso_cfdi,
    fac.folio_fiscal,
    uso.nombre AS nombre_uso_cfdi,
    uso.id AS id_uso_cfdi,
    COALESCE(fac.subtotal, (0)::numeric) AS sub_total,
    COALESCE(fac.tipo_cambio, (0)::numeric) AS tipo_cambio,
    COALESCE(ca.id, 0) AS id_campo,
    ca.nombre AS campo_factura,
    fac.poliza,
    p.rfc AS proveedor_rfc,
    o.url AS url_pdf_compra,
        CASE
            WHEN ((fac.tipo_cambio IS NOT NULL) AND (fac.tipo_cambio > (0)::numeric)) THEN (fac.subtotal * fac.tipo_cambio)
            ELSE fac.subtotal
        END AS sub_total_pesos,
    fe.fecha_genero AS fecha_proceso,
    fe.hora_genero AS hora_proceso,
    (fe.fecha_genero + fe.hora_genero) AS fecha_hora_proceso
   FROM ((((((((((public.si_factura fac
     JOIN public.oc_factura_status fe ON ((fe.si_factura = fac.id)))
     JOIN public.oc_uso_cfdi uso ON ((fac.oc_uso_cfdi = uso.id)))
     JOIN public.estatus e ON ((fe.estatus = e.id)))
     JOIN public.orden o ON ((fac.orden = o.id)))
     JOIN public.ap_campo cam ON ((o.ap_campo = cam.id)))
     JOIN public.compania com ON (((o.compania)::text = (com.rfc)::text)))
     JOIN public.proveedor p ON ((o.proveedor = p.id)))
     JOIN public.moneda m ON ((o.moneda = m.id)))
     JOIN public.ap_campo ca ON ((fac.ap_campo = ca.id)))
     LEFT JOIN public.si_adjunto adj ON ((fac.si_adjunto = adj.id)))
  WHERE ((e.id = 730) AND (fe.actual = true) AND (fe.eliminado = false) AND (fac.eliminado = false));


