--
-- Name: odc_aprobaciones_pendientes_vw; Type: VIEW; Schema: public; Owner: ihsa
--

CREATE VIEW public.odc_aprobaciones_pendientes_vw AS
 SELECT o.ap_campo,
    o.consecutivo,
    upper((o.referencia)::text) AS referencia,
    a.estatus,
    u.email,
    'Visto Bueno'::text AS sigestatus
   FROM (((((public.autorizaciones_orden a
     JOIN public.orden o ON (((o.id = a.orden) AND (o.eliminado = false))))
     JOIN public.ap_campo ac ON (((ac.id = o.ap_campo) AND (ac.eliminado = false))))
     JOIN public.compania c ON ((((c.rfc)::text = (ac.compania)::text) AND (c.eliminado = false))))
     JOIN public.usuario u ON ((((u.id)::text = (a.autoriza_gerencia)::text) AND (u.eliminado = false))))
     JOIN public.estatus e ON ((e.id = a.estatus)))
  WHERE ((a.eliminado = false) AND (a.estatus = 110) AND ((CURRENT_DATE - COALESCE(a.fecha_solicito, CURRENT_DATE)) > 3))
UNION
 SELECT o.ap_campo,
    o.consecutivo,
    upper((o.referencia)::text) AS referencia,
    a.estatus,
    u.email,
    'Revisar'::text AS sigestatus
   FROM ((((public.autorizaciones_orden a
     JOIN public.orden o ON (((o.id = a.orden) AND (o.eliminado = false))))
     JOIN public.ap_campo ac ON (((ac.id = o.ap_campo) AND (ac.eliminado = false))))
     JOIN public.compania c ON ((((c.rfc)::text = (ac.compania)::text) AND (c.eliminado = false))))
     JOIN public.usuario u ON ((((u.id)::text = (a.autoriza_mpg)::text) AND (u.eliminado = false))))
  WHERE ((a.eliminado = false) AND (a.estatus = 120) AND ((CURRENT_DATE - COALESCE(a.fecha_autorizo_gerencia, CURRENT_DATE)) > 3))
UNION
 SELECT o.ap_campo,
    o.consecutivo,
    upper((o.referencia)::text) AS referencia,
    a.estatus,
    u.email,
    'Aprobar'::text AS sigestatus
   FROM ((((public.autorizaciones_orden a
     JOIN public.orden o ON (((o.id = a.orden) AND (o.eliminado = false))))
     JOIN public.ap_campo ac ON (((ac.id = o.ap_campo) AND (ac.eliminado = false))))
     JOIN public.compania c ON ((((c.rfc)::text = (ac.compania)::text) AND (c.eliminado = false))))
     JOIN public.usuario u ON ((((u.id)::text = (a.autoriza_ihsa)::text) AND (u.eliminado = false))))
  WHERE ((a.eliminado = false) AND (a.estatus = 130) AND ((CURRENT_DATE - COALESCE(a.fecha_autorizo_mpg, CURRENT_DATE)) > 3))
UNION
 SELECT o.ap_campo,
    o.consecutivo,
    upper((o.referencia)::text) AS referencia,
    a.estatus,
    u.email,
    'Autorizar'::text AS sigestatus
   FROM ((((public.autorizaciones_orden a
     JOIN public.orden o ON (((o.id = a.orden) AND (o.eliminado = false))))
     JOIN public.ap_campo ac ON (((ac.id = o.ap_campo) AND (ac.eliminado = false))))
     JOIN public.compania c ON ((((c.rfc)::text = (ac.compania)::text) AND (c.eliminado = false))))
     JOIN public.usuario u ON ((((u.id)::text = (a.autoriza_compras)::text) AND (u.eliminado = false))))
  WHERE ((a.eliminado = false) AND (a.estatus = 140) AND ((CURRENT_DATE - COALESCE(a.fecha_autorizo_ihsa, CURRENT_DATE)) > 3))
UNION
 SELECT o.ap_campo,
    o.consecutivo,
    upper((o.referencia)::text) AS referencia,
    a.estatus,
    u.email,
    'Enviar al provedor'::text AS sigestatus
   FROM ((((public.autorizaciones_orden a
     JOIN public.orden o ON (((o.id = a.orden) AND (o.eliminado = false))))
     JOIN public.ap_campo ac ON (((ac.id = o.ap_campo) AND (ac.eliminado = false))))
     JOIN public.compania c ON ((((c.rfc)::text = (ac.compania)::text) AND (c.eliminado = false))))
     JOIN public.usuario u ON ((((u.id)::text = (a.enviarpdf)::text) AND (u.eliminado = false))))
  WHERE ((a.eliminado = false) AND (a.estatus = 150) AND ((CURRENT_DATE - COALESCE(a.fecha_autorizo_compras, CURRENT_DATE)) > 3));


