--
-- Name: oficios_movimientos_vw; Type: VIEW; Schema: public; Owner: ihsa
--

CREATE VIEW public.oficios_movimientos_vw AS
 WITH oficios AS (
         SELECT o.id AS oficio_id,
            o.numero_oficio,
            o.fecha_oficio,
            o.asunto,
            o.ap_campo_gerencia,
            c.nombre AS campo,
            o.estatus,
            o.co_privacidad,
            o.fecha_modifico,
            o.hora_modifico,
            t.id AS tipo_id,
            t.nombre AS tipo_nombre,
            acg.ap_campo,
            g.nombre AS nombre_gerencia,
            e.nombre AS nombre_estatus
           FROM (((((public.of_oficio o
             JOIN public.ap_campo_gerencia acg ON ((o.ap_campo_gerencia = acg.id)))
             JOIN public.gerencia g ON ((acg.gerencia = g.id)))
             JOIN public.ap_campo c ON ((acg.ap_campo = c.id)))
             JOIN public.of_tipo_oficio t ON ((o.of_tipo_oficio = t.id)))
             JOIN public.estatus e ON ((o.estatus = e.id)))
          WHERE ((1 = 1) AND (o.fecha_notifico_alta IS NULL) AND (o.eliminado = false))
        ), movimientos AS (
         SELECT m.id AS mov_id,
            o.oficio_id,
            o.numero_oficio,
            o.fecha_oficio,
            o.asunto,
            o.ap_campo_gerencia,
            o.campo,
            o.estatus,
            o.co_privacidad,
            o.fecha_modifico,
            o.hora_modifico,
            o.tipo_id,
            o.tipo_nombre,
            o.ap_campo,
            o.nombre_gerencia,
            o.nombre_estatus,
            a.id AS adjunto_id,
            a.url,
            a.uuid,
            a.tipo_archivo,
            a.peso,
            oper.id AS oper_id,
            m.fecha_genero AS fecha_genero_mov,
            m.hora_genero AS hora_genero_mov
           FROM (((((public.si_movimiento m
             JOIN public.of_oficio_si_movimiento om ON (((om.si_movimiento = m.id) AND (om.eliminado = false))))
             JOIN oficios o ON ((o.oficio_id = om.of_oficio)))
             JOIN public.si_operacion oper ON (((m.si_operacion = oper.id) AND (oper.eliminado = false))))
             JOIN public.of_oficio_si_mov_si_adjunto oma ON ((om.id = oma.of_oficio_si_movimiento)))
             JOIN public.si_adjunto a ON ((oma.si_adjunto = a.id)))
        )
 SELECT movimientos.mov_id,
    movimientos.oficio_id,
    movimientos.numero_oficio,
    movimientos.fecha_oficio,
    movimientos.asunto,
    movimientos.ap_campo_gerencia,
    movimientos.campo,
    movimientos.estatus,
    movimientos.co_privacidad,
    movimientos.fecha_modifico,
    movimientos.hora_modifico,
    movimientos.tipo_id,
    movimientos.tipo_nombre,
    movimientos.ap_campo,
    movimientos.nombre_gerencia,
    movimientos.nombre_estatus,
    movimientos.adjunto_id,
    movimientos.url,
    movimientos.uuid,
    movimientos.tipo_archivo,
    movimientos.peso,
    movimientos.oper_id,
    movimientos.fecha_genero_mov,
    movimientos.hora_genero_mov
   FROM movimientos;


