CREATE VIEW public.ordenes_estatus_autorizacion_vw AS
 SELECT o.id,
    o.consecutivo,
    o.referencia,
    o.subtotal,
    o.iva,
    o.total,
    o.supera_monto,
    o.total_usd,
    a.fecha_solicito,
