    a.estatus,
    a.autoriza_compras,
    p.nombre AS proveedor_nombre,
    g.nombre AS gerencia_nombre,
    c.id AS ap_campo,
    c.nombre AS campo_nombre,
    m.nombre AS moneda,
    a.autoriza_licitacion
   FROM ((((((public.autorizaciones_orden a
     JOIN public.orden o ON ((a.orden = o.id)))
     JOIN public.proveedor p ON (((o.proveedor = p.id) AND (p.eliminado = false))))
     JOIN public.usuario u ON ((((a.solicito)::text = (u.id)::text) AND (u.eliminado = false))))
     JOIN public.gerencia g ON (((o.gerencia = g.id) AND (g.eliminado = false))))
     JOIN public.ap_campo c ON ((o.ap_campo = c.id)))
     JOIN public.moneda m ON ((o.moneda = m.id)))
  WHERE (o.eliminado = false);


