CREATE VIEW public.rep_proveedores_sgl_vw AS
 SELECT ac.nombre AS campo,
    o.fecha,
    r.referencia,
    p.rfc,
    p.nombre AS proveedor,
    o.consecutivo AS orden,
    po.cuenta_contable AS ot,
    g.nombre AS gerencia,
    po.nombre AS proyecto,
    m.siglas AS moneda,
    round((sum(o.total))::numeric, 2) AS total_prov
   FROM ((((((public.orden o
     JOIN public.ap_campo ac ON ((o.ap_campo = ac.id)))
     JOIN public.requisicion r ON ((o.requisicion = r.id)))
     JOIN public.proyecto_ot po ON ((o.proyecto_ot = po.id)))
     JOIN public.gerencia g ON ((o.gerencia = g.id)))
     JOIN public.proveedor p ON ((o.proveedor = p.id)))
     JOIN public.moneda m ON ((o.moneda = m.id)))
  WHERE ((1 = 1) AND (o.eliminado = false) AND (o.consecutivo IS NOT NULL))
  GROUP BY p.nombre, p.rfc, po.cuenta_contable, r.referencia, o.consecutivo, g.nombre, po.nombre, m.siglas, ac.nombre, o.fecha;


