--
-- Name: reporte_vencimiento_contratos; Type: VIEW; Schema: public; Owner: ihsa
--

CREATE VIEW public.reporte_vencimiento_contratos AS
 WITH por_vencer AS (
         SELECT max(convenio.id) AS id,
            convenio.convenio
           FROM public.convenio
          WHERE ((1 = 1) AND (convenio.convenio IS NOT NULL) AND (convenio.eliminado = false) AND ((convenio.fecha_vencimiento >= (now())::date) AND (convenio.fecha_vencimiento <= ((now() + '30 days'::interval))::date)))
          GROUP BY convenio.convenio
        )
 SELECT c.id,
    c.codigo,
    c.nombre AS convenio_nombre,
    c.monto,
    c.fecha_vencimiento,
    c.ap_campo,
    p.nombre AS proveedor_nombre,
    m.siglas
   FROM (((public.convenio c
     JOIN por_vencer pv ON ((c.id = pv.id)))
     JOIN public.proveedor p ON ((c.proveedor = p.id)))
     JOIN public.moneda m ON ((c.moneda = m.id)))
  WHERE ((1 = 1) AND (c.eliminado = false))
UNION
 SELECT c.id,
    c.codigo,
    c.nombre AS convenio_nombre,
    c.monto,
    c.fecha_vencimiento,
    c.ap_campo,
    p.nombre AS proveedor_nombre,
    m.siglas
   FROM ((public.convenio c
     JOIN public.proveedor p ON ((c.proveedor = p.id)))
     JOIN public.moneda m ON ((c.moneda = m.id)))
  WHERE ((1 = 1) AND (c.eliminado = false) AND ((c.fecha_vencimiento >= (now())::date) AND (c.fecha_vencimiento <= ((now() + '30 days'::interval))::date)) AND (c.convenio IS NULL) AND (NOT (c.id IN ( SELECT convenio.convenio
           FROM public.convenio
          WHERE ((1 = 1) AND (convenio.convenio IS NOT NULL) AND (convenio.eliminado = false))))));


