--
-- Name: reporte_viajes_por_salir_vw; Type: VIEW; Schema: public; Owner: ihsa
--

CREATE VIEW public.reporte_viajes_por_salir_vw AS
 SELECT v.id AS viaje_id,
    v.codigo,
    e.id AS estatus_id,
    e.nombre AS nombre_estatus,
    v.autobus,
    v.vehiculo_propio,
    v.vehiculo_asignado_empresa,
    v.responsable,
    v.fecha_salida,
    v.hora_salida,
    v.fecha_regreso,
    v.hora_regreso,
    v.sg_oficina,
    COALESCE(v.sg_viaje, 0) AS sg_viaje,
    COALESCE(v.sg_viaje_ciudad, 0) AS sg_viaje_ciudad,
    COALESCE(rt.id, 0) AS rt_id,
    rt.nombre AS nombre_rt,
    COALESCE(v.sg_oficina, 0) AS sg_oficina_dest,
    v.si_adjunto,
    u.nombre AS usuario_responsable,
    v.redondo,
    v.fecha_programada,
    v.hora_programada,
    ad.uuid AS uuid_adjunto,
    ve.capacidad_pasajeros,
        CASE
            WHEN (drt.id > 0) THEN ot.nombre
            ELSE c.nombre
        END AS nombre,
    rt.tiempo_viaje,
    v.con_intercepcion AS intercepcion,
    ori.nombre AS nombre_origen,
    u.telefono,
    v.sg_tipo_especifico,
    v.eliminado,
    v.conchofer
   FROM (((((((((((public.sg_viaje v
     JOIN public.sg_ruta_terrestre rt ON (((rt.id = v.sg_ruta_terrestre) AND (rt.eliminado = false))))
     JOIN public.estatus e ON (((e.id = v.estatus) AND (e.eliminado = false))))
     JOIN public.usuario u ON ((((u.id)::text = (v.responsable)::text) AND (u.eliminado = false))))
     JOIN public.sg_viaje_vehiculo vv ON (((vv.sg_viaje = v.id) AND (vv.eliminado = false))))
     JOIN public.sg_vehiculo ve ON (((ve.id = vv.sg_vehiculo) AND (ve.eliminado = false))))
     JOIN public.sg_oficina ori ON (((ori.id = v.sg_oficina) AND (ori.eliminado = false))))
     LEFT JOIN public.sg_detalle_ruta_terrestre drt ON (((drt.sg_ruta = rt.id) AND (drt.eliminado = false))))
     LEFT JOIN public.sg_detalle_ruta_ciudad drc ON (((drc.sg_ruta_terrestre = rt.id) AND (drc.eliminado = false))))
     LEFT JOIN public.sg_oficina ot ON (((ot.id = drt.sg_oficina) AND (ot.eliminado = false))))
     LEFT JOIN public.si_ciudad c ON (((c.id = drc.si_ciudad) AND (c.eliminado = false))))
     LEFT JOIN public.si_adjunto ad ON ((v.si_adjunto = ad.id)));


