CREATE VIEW public.usuario_rol_campo_vw AS
 SELECT ur.ap_campo,
    c.nombre AS campo_nombre,
    ur.id AS usuario_rol_id,
    u.id AS usuario_id,
    u.nombre AS usuario_nombre,
    r.id AS rol_id,
    r.nombre AS rol_nombre,
    ur.is_principal,
    u.email,
    u.telefono,
    r.si_modulo
   FROM (((public.si_usuario_rol ur
     JOIN public.usuario u ON ((((ur.usuario)::text = (u.id)::text) AND (u.activo = true))))
     JOIN public.si_rol r ON ((r.id = ur.si_rol)))
     JOIN public.ap_campo c ON ((ur.ap_campo = c.id)))
  WHERE ((ur.eliminado = false) AND (u.eliminado = false));


