--
-- Name: viajeros_por_solicitud_vw; Type: VIEW; Schema: public; Owner: ihsa
--

CREATE VIEW public.viajeros_por_solicitud_vw AS
 SELECT DISTINCT v.sg_solicitud_viaje,
    COALESCE(u.nombre, i.nombre) AS nombre_viajero,
        CASE
            WHEN (u.nombre IS NULL) THEN 20
            ELSE 19
        END AS tipo_viajero,
    v.eliminado AS viajero_eliminado
   FROM ((public.sg_viajero v
     LEFT JOIN public.usuario u ON ((((v.usuario)::text = (u.id)::text) AND (u.eliminado = false))))
     LEFT JOIN public.sg_invitado i ON (((v.sg_invitado = i.id) AND (i.eliminado = false))));


