 SELECT DISTINCT v.id,
    COALESCE(u.nombre, i.nombre) AS viajero,
    COALESCE(u.telefono, i.telefono) AS telefono
   FROM (((public.sg_viaje v
     LEFT JOIN public.sg_viajero vi ON (((v.id = vi.sg_viaje) AND (vi.eliminado = false))))
     LEFT JOIN public.usuario u ON (((vi.usuario)::text = (u.id)::text)))
