-- Name: contenido_nacional; Type: VIEW; Schema: reporting; Owner: ihsa
--

CREATE VIEW reporting.contenido_nacional AS
 SELECT com.rfc AS compania_id,
    com.nombre AS compania_nombre,
    c.id AS campo_id,
    c.nombre AS campo_nombre,
    f.id AS factura_id,
    p.id AS proveedor_id,
    p.nombre AS proveedor_nombre,
    p.rfc,
    a.fecha_genero AS fecha_carta,
    f.folio AS factura_folio,
    o.contrato,
    o.consecutivo AS orden_consecutivo,
    ger.nombre AS gerencia_nombre,
    ch.codigo AS codigo_catalogo,
    g.titulo AS grupo_titulo,
    g.nombre AS grupo_descripcion,
    ch.nombre AS descripcion_catalogo,
    ch.nombre AS descripcion_carta,
    fcn.contenido_nacional,
    round(fcn.monto_facturado, 2) AS monto_facturado_cn,
    round((fcn.contenido_nacional * fcn.monto_facturado), 2) AS monto_afectado_cn
   FROM ((((((((((public.si_factura f
     JOIN public.oc_factura_status fe ON ((fe.si_factura = f.id)))
     JOIN public.si_adjunto a ON ((f.si_adjunto = a.id)))
     JOIN public.orden o ON (((o.id = f.orden) AND (o.eliminado = false))))
     JOIN public.gerencia ger ON ((o.gerencia = ger.id)))
     JOIN public.proveedor p ON (((p.id = f.proveedor) AND (p.eliminado = false))))
     JOIN public.si_factura_contenido_nacional fcn ON (((fcn.si_factura = f.id) AND (fcn.eliminado = false))))
     JOIN public.si_catalogo_hidrocarburo ch ON (((ch.id = fcn.si_catalogo_hidrocarburo) AND (ch.eliminado = false))))
     JOIN public.si_grupo g ON ((ch.si_grupo = g.id)))
     JOIN public.ap_campo c ON ((o.ap_campo = c.id)))
     JOIN public.compania com ON (((c.compania)::text = (com.rfc)::text)))
  WHERE ((1 = 1) AND (fe.actual = true) AND (fe.estatus >= 730) AND (f.si_factura IS NULL));


