--

CREATE VIEW reporting.factura_encabezado AS
 SELECT f.id AS factura_id,
    cia.rfc AS compania_id,
    cia.nombre AS compania_nombre,
    cam.id AS bloque_id,
    cam.nombre AS bloque_nombre,
    prov.id AS proveedor_id,
    prov.rfc AS proveedor_rfc,
    prov.nombre AS proveedor_nombre,
    f.folio AS factura_folio,
    o.id AS orden_id,
    o.consecutivo AS orden_consecutivo,
    g.nombre AS gerencia_nombre,
    e.nombre AS estatus,
    uc.nombre AS uso_cfdi,
    f.subtotal,
    m.siglas AS moneda,
    f.tipo_cambio,
        CASE
            WHEN (COALESCE(f.tipo_cambio, (0)::numeric) = ANY (ARRAY[(0)::numeric, (1)::numeric])) THEN f.subtotal
            ELSE (f.subtotal * f.tipo_cambio)
        END AS subtotal_moneda_nacional,
    f.fecha_emision
   FROM (((((((((public.si_factura f
     JOIN public.compania cia ON (((f.compania)::text = (cia.rfc)::text)))
     JOIN public.ap_campo cam ON ((f.ap_campo = cam.id)))
     JOIN public.orden o ON (((f.orden = o.id) AND (o.eliminado = false))))
     JOIN public.proveedor prov ON ((f.proveedor = prov.id)))
     JOIN public.oc_factura_status fs ON (((f.id = fs.si_factura) AND (fs.actual = true) AND (fs.estatus >= 730))))
     JOIN public.gerencia g ON ((o.gerencia = g.id)))
     JOIN public.estatus e ON ((fs.estatus = e.id)))
     JOIN public.oc_uso_cfdi uc ON ((f.oc_uso_cfdi = uc.id)))
     JOIN public.moneda m ON ((f.moneda = m.id)))
  WHERE ((1 = 1) AND (f.eliminado = false) AND (f.folio_fiscal IS NOT NULL));


