--
-- Name: orden_detalle; Type: VIEW; Schema: reporting; Owner: ihsa
--

CREATE VIEW reporting.orden_detalle AS
 SELECT o.id AS orden_id,
    o.consecutivo AS orden_consecutivo,
    (('20'::text || "substring"((o.consecutivo)::text, ("position"((o.consecutivo)::text, '-'::text) - 2), 2)))::integer AS consecutivo_anio,
    o.gerencia AS gerencia_id,
    g.nombre AS gerencia,
    o.ap_campo AS bloque_id,
    c.nombre AS bloque,
    ao.estatus AS estatus_id,
    e.nombre AS estatus,
    od.id AS orden_detalle_id,
    od.codigo,
    od.descripcion,
    od.cantidad,
    od.unidad,
    o.moneda AS moneda_id,
    m.nombre AS moneda_nombre,
    od.precio_unitario,
    od.importe,
        CASE
            WHEN ((m.nombre)::text = 'MXN'::text) THEN (od.importe / ( SELECT COALESCE(paridad_valor.valor, (0)::double precision) AS "coalesce"
               FROM public.paridad_valor
              WHERE (paridad_valor.paridad = pa.id)
              ORDER BY paridad_valor.id DESC
             LIMIT 1))
            ELSE od.importe
        END AS valor_usd,
    od.observaciones,
    od.cantidad_recibida,
    ia.id AS articulo_id,
    ia.codigo AS articulo_codigo,
    ((ia.nombre)::text ||
        CASE
            WHEN ((od.textnav IS NULL) OR ((od.textnav)::text = ''::text)) THEN ''::text
            ELSE (' '::text || (od.textnav)::text)
        END) AS articulo_nombre,
    od.textnav,
    u.id AS unidad_id,
    u.nombre AS unidad_nombre,
    od.recibido
   FROM (((((((((public.orden_detalle od
     JOIN public.inv_articulo ia ON ((od.inv_articulo = ia.id)))
     JOIN public.orden o ON (((od.orden = o.id) AND (o.consecutivo IS NOT NULL) AND (o.eliminado = false))))
     LEFT JOIN public.moneda m ON ((o.moneda = m.id)))
     LEFT JOIN public.si_unidad u ON ((u.id = od.si_unidad)))
     LEFT JOIN public.gerencia g ON ((g.id = o.gerencia)))
     JOIN public.ap_campo c ON (((c.id = o.ap_campo) AND (c.eliminado = false))))
     JOIN public.autorizaciones_orden ao ON ((ao.orden = o.id)))
     JOIN public.estatus e ON ((e.id = ao.estatus)))
     LEFT JOIN public.paridad pa ON ((((pa.compania)::text = (c.compania)::text) AND (pa.eliminado = false) AND (pa.monedades = m.id) AND ((pa.nombre)::text ~ 'USD'::text))))
  WHERE ((1 = 1) AND (od.eliminado = false));


