CREATE VIEW reporting.orden_encabezado AS
 SELECT o.id AS orden_id,
    o.consecutivo,
    o.navcode AS pedido_navision,
    req.id AS requisicion_id,
    req.consecutivo AS requisicion_consecutivo,
    u1.id AS usuario_id,
    u1.nombre AS analista_nombre,
    g.id AS gerencia_id,
    g.nombre AS gerencia_nombre,
    m.id AS moneda_id,
    m.nombre AS moneda_nombre,
    p.id AS proveedor_id,
    p.nombre AS proveedor_nombre,
    c.rfc AS compania_id,
    c.nombre AS compania_nombre,
    cam.id AS campo_id,
    cam.nombre AS campo_nombre,
    cp.id AS condicion_pago_id,
    cp.nombre AS condicion_pago_nombre,
    ofp.id AS forma_pago_id,
    ofp.nombre AS forma_pago_nombre,
    ouc.id AS unidad_costo_id,
    ouc.nombre AS unidad_costo_nombre,
    sc.id AS subcampo_id,
    sc.nombre AS subcampo_nombre,
    e.id AS estatus_id,
    e.nombre AS estatus,
    (ao.fecha_solicito + ao.hora_solicito) AS fecha_solicito,
    (ao.fecha_autorizo_gerencia + ao.hora_autorizo_gerencia) AS autorizo_gerencia,
    (ao.fecha_autorizo_compras + ao.hora_autorizo_compras) AS autorizo_compras,
    (ao.fecha_autorizo_finanzas + ao.hora_autorizo_finanzas) AS autorizo_finanzas,
    (ao.fecha_autorizo_mpg + ao.hora_autorizo_mpg) AS autorizo_mpg,
    (ao.fecha_autorizo_ihsa + ao.hora_autorizo_ihsa) AS autorizo_ihsa,
    (ao.fecha_autorizo_licitacion + ao.hora_autorizo_licitacion) AS autorizo_licitacion,
    (ao.fecha_envio_proveedor + ao.hora_envio_proveedor) AS envio_proveedor,
    o.subtotal,
    o.impuesto,
    o.total,
    o.total_usd,
    tc.id AS termino_pago_id,
    tc.nombre AS termino_pago_nombre
   FROM ((((((((((((((public.orden o
     JOIN public.autorizaciones_orden ao ON ((ao.orden = o.id)))
     JOIN public.usuario u1 ON (((u1.id)::text = (o.analista)::text)))
     JOIN public.gerencia g ON ((g.id = o.gerencia)))
     JOIN public.moneda m ON ((m.id = o.moneda)))
     JOIN public.proveedor p ON ((p.id = o.proveedor)))
     JOIN public.compania c ON (((c.rfc)::text = (o.compania)::text)))
     JOIN public.ap_campo cam ON ((cam.id = o.ap_campo)))
     JOIN public.oc_unidad_costo ouc ON ((ouc.id = o.oc_unidad_costo)))
     JOIN public.estatus e ON ((ao.estatus = e.id)))
     JOIN public.requisicion req ON ((o.requisicion = req.id)))
     LEFT JOIN public.condicion_pago cp ON ((cp.id = o.condicion_pago)))
     LEFT JOIN public.oc_forma_pago ofp ON ((ofp.id = o.oc_forma_pago)))
     LEFT JOIN public.oc_subcampo sc ON ((sc.id = o.oc_subcampo)))
     LEFT JOIN public.oc_termino_pago tc ON ((tc.id = o.oc_termino_pago)))
  WHERE (o.consecutivo IS NOT NULL);


