--

CREATE VIEW reporting.ordenes_inventario AS
 SELECT o.id AS orden_id,
    o.consecutivo AS orden_consecutivo,
    o.ap_campo AS campo_id,
    cam.nombre AS campo_nombre,
    c.rfc AS compania_id,
    c.nombre AS compania_nombre,
    it.folio_orden_compra,
    it.fecha,
    a.id AS articulo_id,
    a.nombre AS articulo_nombre,
    ita.numero_unidades,
    ita.identificador,
    it.notas,
    alm.nombre AS almacen_nombre,
        CASE
            WHEN (it.tipo_movimiento = 1) THEN 'Entrada'::text
            WHEN (it.tipo_movimiento = 2) THEN 'Salida'::text
            WHEN (it.tipo_movimiento = 3) THEN 'Traspaso Entrante'::text
            WHEN (it.tipo_movimiento = 4) THEN 'Traspaso Saliente'::text
            WHEN (it.tipo_movimiento = 5) THEN 'Merma'::text
            WHEN (it.tipo_movimiento = 6) THEN 'Perdida'::text
            ELSE 'Desconocido'::text
        END AS tipo_movimiento
   FROM ((((((public.orden o
     JOIN public.inv_transaccion it ON ((((it.folio_orden_compra)::text = (o.consecutivo)::text) AND (it.status = 2))))
     JOIN public.inv_transaccion_articulo ita ON ((ita.transaccion = it.id)))
     JOIN public.inv_articulo a ON ((ita.articulo = a.id)))
     JOIN public.inv_almacen alm ON ((it.almacen = alm.id)))
     JOIN public.ap_campo cam ON ((o.ap_campo = cam.id)))
     JOIN public.compania c ON (((o.compania)::text = (c.rfc)::text)))
  WHERE ((1 = 1) AND (o.eliminado = false));


