     JOIN public.moneda m ON ((o.moneda = m.id)))
     LEFT JOIN public.si_unidad su ON ((od.si_unidad = su.id)))
     LEFT JOIN public.inv_articulo ia ON ((od.inv_articulo = ia.id)))
