CREATE VIEW reporting.reporte_incidencias AS
 SELECT c.nombre AS nombre_compania,
    ac.nombre AS nombre_campo,
    ci.nombre AS categoria,
    si.codigo AS numero_ticket,
    u.nombre AS creado_por,
    g.nombre AS nombre_gerencia,
    e.nombre AS nombre_estatus,
    e.id AS estatus_id,
    n.nombre AS nivel,
    si.duracion,
    si.fecha_genero,
    si.hora_genero,
    si.fecha_modifico AS fecha_ultimo_cambio,
    si.hora_modifico AS hora_ultimo_cambio,
    ug.nombre AS atendido_por,
    si.titulo
   FROM ((((((((public.si_incidencia si
     JOIN public.usuario u ON (((si.genero)::text = (u.id)::text)))
     JOIN public.gerencia g ON ((si.gerencia = g.id)))
     JOIN public.estatus e ON ((si.estado = e.id)))
     JOIN public.si_categoria_incidencia ci ON ((si.si_categoria_incidencia = ci.id)))
     JOIN public.ap_campo ac ON ((ac.id = si.ap_campo)))
     JOIN public.compania c ON (((ac.compania)::text = (c.rfc)::text)))
     JOIN public.si_nivel n ON ((si.si_nivel = n.id)))
     LEFT JOIN public.usuario ug ON (((si.asignado_a)::text = (ug.id)::text)))
