CREATE VIEW reporting.reporte_tickets_atendidos AS
 SELECT DISTINCT ON (i.codigo) c.nombre AS compania,
    ac.nombre AS campo,
    i.codigo,
    e.nombre AS nombre_estatus,
    i.titulo,
    ci.nombre AS categoria_incidencia,
    g.nombre AS gerencia,
    u1.nombre AS usuario_reporto,
    pr.nombre AS prioridad,
    (i.fecha_genero + i.hora_genero) AS fecha_hora_creacion,
    (im.fecha_genero + im.hora_genero) AS fecha_hora_cierre,
    ((im.fecha_genero + im.hora_genero) - (i.fecha_genero + i.hora_genero)) AS tiempo_vida_ticket,
    i.duracion,
    n.nombre AS nivel,
    i.motivo_escala
   FROM (((((((((public.si_incidencia i
     JOIN public.usuario u1 ON (((i.genero)::text = (u1.id)::text)))
     JOIN public.si_incidencia_movimientos im ON (((im.si_incidencia = i.id) AND (im.estatus = 1260))))
     JOIN public.si_nivel n ON ((i.si_nivel = n.id)))
     JOIN public.si_categoria_incidencia ci ON ((i.si_categoria_incidencia = ci.id)))
     JOIN public.prioridad pr ON ((i.prioridad = pr.id)))
     JOIN public.gerencia g ON ((i.gerencia = g.id)))
     JOIN public.ap_campo ac ON ((i.ap_campo = ac.id)))
     JOIN public.compania c ON (((ac.compania)::text = (c.rfc)::text)))
     JOIN public.estatus e ON ((i.estado = e.id)))
  WHERE ((1 = 1) AND (i.eliminado = false) AND (i.estado = 1260))
  ORDER BY i.codigo;


