--
-- Name: seguimiento_factura_vw; Type: VIEW; Schema: reporting; Owner: ihsa
--

CREATE VIEW reporting.seguimiento_factura_vw AS
 SELECT facturas.factura_id,
    facturas.compania_nombre,
    facturas.campo_nombre,
    facturas.orden_consecutivo,
    facturas.gerencia_nombre,
    facturas.proveedor_nombre,
    facturas.orden_fecha,
    facturas.orden_destino,
    facturas.orden_moneda_siglas,
    facturas.orden_subtotal,
    facturas.orden_impuesto,
    facturas.orden_total,
    facturas.orden_total_usd,
    facturas.orden_estatus,
    facturas.factura_folio,
    facturas.factura_fecha_emision,
    facturas.factura_estatus,
    facturas.factura_moneda_siglas,
    facturas.factura_subtotal,
    facturas.factura_impuestos,
    facturas.factura_total,
    facturas.termino_pago,
    facturas.termino_pago_dias,
    ef.factura_fecha_envio,
    faf.fecha_acepto_finanzas,
    fap.fecha_pagada,
        CASE
            WHEN (faf.fecha_acepto_finanzas IS NOT NULL) THEN (faf.fecha_acepto_finanzas + (facturas.termino_pago_dias)::integer)
            ELSE NULL::date
        END AS fecha_pago_tentativa
   FROM (((( SELECT f.id AS factura_id,
            co.nombre AS compania_nombre,
            c.nombre AS campo_nombre,
            o.consecutivo AS orden_consecutivo,
            g.nombre AS gerencia_nombre,
            p.nombre AS proveedor_nombre,
            o.fecha AS orden_fecha,
            o.destino AS orden_destino,
            m.siglas AS orden_moneda_siglas,
            o.subtotal AS orden_subtotal,
            o.iva AS orden_impuesto,
            o.total AS orden_total,
            o.total_usd AS orden_total_usd,
            e.nombre AS orden_estatus,
            f.folio AS factura_folio,
            f.fecha_emision AS factura_fecha_emision,
            ef_1.nombre AS factura_estatus,
            m.siglas AS factura_moneda_siglas,
            f.subtotal AS factura_subtotal,
            (f.monto - (f.subtotal)::double precision) AS factura_impuestos,
            f.monto AS factura_total,
            tp.nombre AS termino_pago,
            to_number((
                CASE
                    WHEN ((tp.codigo)::text ~~ '%D'::text) THEN (rtrim((tp.codigo)::text, 'D'::text))::character varying
                    ELSE tp.codigo
                END)::text, '000'::text) AS termino_pago_dias
           FROM (((((((((((public.orden o
             JOIN public.autorizaciones_orden ao ON (((ao.orden = o.id) AND (ao.eliminado = false))))
             JOIN public.estatus e ON ((ao.estatus = e.id)))
             JOIN public.ap_campo c ON ((o.ap_campo = c.id)))
             JOIN public.compania co ON (((o.compania)::text = (co.rfc)::text)))
             LEFT JOIN public.oc_termino_pago tp ON ((o.oc_termino_pago = tp.id)))
             LEFT JOIN public.gerencia g ON ((o.gerencia = g.id)))
             LEFT JOIN public.moneda m ON ((o.moneda = m.id)))
             LEFT JOIN public.si_factura f ON (((f.orden = o.id) AND (f.eliminado = false))))
             LEFT JOIN public.proveedor p ON ((o.proveedor = p.id)))
             LEFT JOIN public.oc_factura_status fs ON (((f.id = fs.si_factura) AND (fs.actual = true) AND (fs.eliminado = false))))
             LEFT JOIN public.estatus ef_1 ON ((fs.estatus = ef_1.id)))
          WHERE ((1 = 1) AND (o.eliminado = false) AND (o.consecutivo IS NOT NULL) AND (o.fecha_genero IS NOT NULL))) facturas
     LEFT JOIN LATERAL ( SELECT fs.fecha_genero AS factura_fecha_envio
           FROM public.oc_factura_status fs
          WHERE ((fs.eliminado = false) AND (fs.estatus = 720) AND (fs.si_factura = facturas.factura_id))
          ORDER BY fs.fecha_genero DESC
         LIMIT 1) ef ON (true))
     LEFT JOIN LATERAL ( SELECT fs.fecha_genero AS fecha_acepto_finanzas
           FROM public.oc_factura_status fs
          WHERE ((fs.eliminado = false) AND (fs.estatus = 740) AND (fs.si_factura = facturas.factura_id))
          ORDER BY fs.fecha_genero DESC
         LIMIT 1) faf ON (true))
     LEFT JOIN LATERAL ( SELECT fs.fecha_genero AS fecha_pagada
           FROM public.oc_factura_status fs
          WHERE ((fs.eliminado = false) AND (fs.estatus = 750) AND (fs.si_factura = facturas.factura_id))
          ORDER BY fs.fecha_genero DESC
         LIMIT 1) fap ON (true))
  WHERE ((1 = 1) AND (facturas.factura_id IS NOT NULL));


