-- Name: tiempos_aprobaciones; Type: VIEW; Schema: reporting; Owner: ihsa
--

CREATE VIEW reporting.tiempos_aprobaciones AS
 WITH autorizaciones AS (
         SELECT com.rfc AS compania_id,
            com.nombre AS compania_nombre,
            c.id AS campo_id,
            c.nombre AS campo_nombre,
            a_1.id,
            o.id AS orden_id,
            o.consecutivo AS orden_consecutivo,
            a_1.estatus,
            e.nombre AS estatus_nombre,
            r.asigna,
            (r.fecha_asigno + r.hora_asigno) AS fecha_asigna,
            a_1.solicito,
            (a_1.fecha_solicito + a_1.hora_solicito) AS fecha_solicita,
            a_1.autoriza_gerencia AS vo_bo,
            (a_1.fecha_autorizo_gerencia + a_1.hora_autorizo_gerencia) AS fecha_vo_bo,
            a_1.autoriza_mpg AS revisa,
            (a_1.fecha_autorizo_mpg + a_1.hora_autorizo_mpg) AS fecha_revisa,
            a_1.autoriza_ihsa AS aprueba,
            (a_1.fecha_autorizo_ihsa + a_1.hora_autorizo_ihsa) AS fecha_aprueba,
            a_1.autoriza_finanzas AS externo,
            (a_1.fecha_autorizo_finanzas + a_1.hora_autorizo_finanzas) AS fecha_externo,
            a_1.autoriza_compras AS autoriza,
            (a_1.fecha_autorizo_compras + a_1.hora_autorizo_compras) AS fecha_autoriza,
            a_1.autoriza_licitacion,
            (a_1.fecha_autorizo_licitacion + a_1.hora_autorizo_licitacion) AS fecha_autoriza_licitacion,
            (a_1.fecha_envio_proveedor + a_1.hora_envio_proveedor) AS fecha_envio_proveedor
           FROM (((((public.autorizaciones_orden a_1
             JOIN public.orden o ON ((o.id = a_1.orden)))
             JOIN public.requisicion r ON (((o.requisicion = r.id) AND (r.eliminado = false))))
             JOIN public.ap_campo c ON ((c.id = o.ap_campo)))
             JOIN public.estatus e ON ((e.id = a_1.estatus)))
             JOIN public.compania com ON (((com.rfc)::text = (c.compania)::text)))
          WHERE ((1 = 1) AND (o.eliminado = false) AND (a_1.eliminado = false))
        )
 SELECT a.id,
    a.campo_id,
    a.campo_nombre,
    a.compania_id,
    a.compania_nombre,
    a.orden_id,
    a.orden_consecutivo,
    a.estatus,
    a.estatus_nombre,
    a.asigna,
    a.fecha_asigna,
    a.solicito,
    a.fecha_solicita,
        CASE
            WHEN ((a.fecha_asigna IS NOT NULL) AND (a.fecha_solicita IS NOT NULL)) THEN date_part('day'::text, (a.fecha_solicita - a.fecha_asigna))
            ELSE (0)::double precision
        END AS dias_solicita,
    a.vo_bo,
    a.fecha_vo_bo,
        CASE
            WHEN ((a.fecha_vo_bo IS NOT NULL) AND (a.fecha_solicita IS NOT NULL)) THEN date_part('day'::text, (a.fecha_vo_bo - a.fecha_solicita))
            ELSE (0)::double precision
        END AS dias_vo_bo,
    a.revisa,
    a.fecha_revisa,
        CASE
            WHEN ((a.fecha_revisa IS NOT NULL) AND (a.fecha_vo_bo IS NOT NULL)) THEN date_part('day'::text, (a.fecha_revisa - a.fecha_vo_bo))
            ELSE (0)::double precision
        END AS dias_revisa,
    a.aprueba,
    a.fecha_aprueba,
        CASE
            WHEN ((a.fecha_aprueba IS NOT NULL) AND (a.fecha_revisa IS NOT NULL)) THEN date_part('day'::text, (a.fecha_aprueba - a.fecha_revisa))
            ELSE (0)::double precision
        END AS dias_aprueba,
    a.externo,
    a.fecha_externo,
        CASE
            WHEN ((a.fecha_externo IS NOT NULL) AND (a.fecha_aprueba IS NOT NULL)) THEN date_part('day'::text, (a.fecha_externo - a.fecha_aprueba))
            ELSE (0)::double precision
        END AS dias_externo,
    a.autoriza,
    a.fecha_autoriza,
        CASE
            WHEN ((a.fecha_autoriza IS NOT NULL) AND (a.fecha_externo IS NOT NULL)) THEN date_part('day'::text, (a.fecha_autoriza - a.fecha_externo))
            WHEN ((a.fecha_externo IS NULL) AND (a.fecha_aprueba IS NOT NULL)) THEN date_part('day'::text, (a.fecha_autoriza - a.fecha_aprueba))
            ELSE (0)::double precision
        END AS dias_autoriza,
    a.autoriza_licitacion,
    a.fecha_autoriza_licitacion,
        CASE
            WHEN ((a.fecha_autoriza_licitacion IS NOT NULL) AND (a.fecha_autoriza IS NOT NULL)) THEN date_part('day'::text, (a.fecha_autoriza_licitacion - a.fecha_autoriza))
            ELSE (0)::double precision
        END AS dias_autoriza_licitacion,
    a.fecha_envio_proveedor,
        CASE
            WHEN ((a.fecha_envio_proveedor IS NOT NULL) AND (a.fecha_autoriza_licitacion IS NOT NULL)) THEN date_part('day'::text, (a.fecha_envio_proveedor - a.fecha_autoriza_licitacion))
            ELSE (0)::double precision
        END AS dias_envio_proveedor
   FROM autorizaciones a;


